const defaults = {
  width: 16,
  height: 16
};

var Player = function(x, y, width, height) {
  this.x = x;
  this.y = y;
  this.width = width || defaults.width;
  this.height = height || defaults.height;
}

Player.prototype.render = function (delta) {
  console.log('player at pos ' + this.x + ' ' + this.y);
};

module.exports = Player;
